import initialState from "../store/initialState"
import * as Actions from './actions'

export const cartReducer = (state = initialState.cart, action) => {
    switch (action.type) {
        case Actions.ADD_CART:
            return {
                list: action.list,
                subtotal:action.subtotal
            }
        default:
            return state
    }
}
