import React, {useState} from "react";
import burgerImg from "../../assets/img/burger.png";
import likeIcon from "../../assets/img/like-icon.svg";
import cartBtn from "../../assets/img/cart-btn.svg";
import { useDispatch, useSelector } from "react-redux";
import { addCart } from "../../reducks/cart/operations";
import { useEffect } from "react";

const Item = ({ setShowWriteReview, setShowReview, item }) => {
  const dispatch = useDispatch();
  const [carts, setCarts]= useState()
  // setCarts(useSelector((state) => state.carts.list))
  let selected_count  = 0
  useEffect(() => {
    if (carts[item.id] && carts[item.id].selected_count !== 0) {
      selected_count = carts[item.id].selected_count
    } else {
      selected_count = 0
    }

  }, [carts, dispatch])

  const addToCart = () => {
    dispatch(addCart(item));
  };
  return (
    <>
      <div class="item">
        <div class="item-image">
          <img
            src={`https://res.cloudinary.com/dv4r9syat/${item.img}`}
            alt="burger"
          />
        </div>
        <div class="item-info">
          <div class="like">
            <img src={likeIcon} alt="like-icon" />
          </div>
          <div class="name">
            <p>{item.name}</p>
          </div>
          <div class="item-review">
            <p onClick={() => setShowWriteReview(true)}>Write Review</p>
            <p onClick={() => setShowReview(true)}>Check Review</p>
          </div>
          <div class="item-price">
            {selected_count == 0 ?
              <img onClick={() => addToCart()} src={cartBtn} alt="cart-btn" />

              :
              <>
                <div>+</div>
                <div>{selected_count}</div>
                <div>-</div>
              </>

            }
            <p> ${item.price}</p>
          </div>
        </div>
      </div>
    </>
  );
};

export default Item;
